import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';

import styles from './Feature.scss';


class Feature extends PureComponent {

	static propTypes = {
		className: PropTypes.string,
		title: PropTypes.string,
		description: PropTypes.string,
		color: PropTypes.string,
		background_image: PropTypes.string,
		image: PropTypes.string,
		text_position: PropTypes.string,
	}

	static defaultProps = {
		text_position: 'right'
	}


	render() {
		const {
			className,
			title,
			description,
			color,
			background_image,
			image,
			text_position,
		} = this.props;

		const container_style = {
			backgroundImage: background_image && `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url(${background_image})`
		};

		const image_style = {
			backgroundImage: image && `url(${image})`,
		};

		const inner_container_style = {
			flexDirection: text_position === 'right' ? 'row-reverse' : 'row',
		};

		const title_description_style = {
			textAlign: text_position === 'right' ? 'right' : 'left',
		}

		return (
			<div className={classnames(styles('container'), className)} style={container_style}>
				<div className={styles('inner-container')} style={inner_container_style}>
					<div className={styles('title-description')} style={title_description_style}>
						<h3 className={styles('title')}>{title}</h3>
						<p className={styles('description')}>{description}</p>
					</div>
					<div className={styles('image')} style={image_style} />
				</div>
			</div>
		);	
	}
}

export default Feature;
