import { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { withRouter } from 'next/router';
import class_schedule from 'static/class_schedule.json';
import schools from 'static/schools.json';
import Header from 'components/header';
import HeroBanner from 'components/herobanner';
import Layout from 'components/layout';
import PageHeader from 'components/pageheader';
import Table from 'components/table';

import styles from './Classes.scss';

import PropTypes from 'prop-types';

class Classes extends Component {
	static getInitialProps ({ reduxStore, req }) {
		const isServer = !!req

		return {}
	}

	static propTypes = {
		router: PropTypes.object,
	}

	constructor(props) {
		super(props);

		this.classes_table = class_schedule;
	}

	renderInstructor = instructor => {
		return (
			<a className={styles('instructor')} href={`${instructor.url}`}>{instructor.name}</a>
		);
	}

	renderSchool = (school, i) => {
		return (
			<section key={`school-${i}`}>
				<h3 className={styles('title')}>school.address.city</h3>
				<div className={styles('info-container')}>
					{school.head && 
						<span className={styles('label')}>Head Instructor</span>
					}
					{school.head && 
						this.renderInstructor(school.head)
					}
					{school.instructors && 
						<span className={styles('label')}>{school.instructors.length > 1 ? 'Instructors' : 'Instructor'}</span>
					}
					{school.instructors && 
						school.instructors.map(this.renderInstructor)
					}
					{school.address.street &&
						<div className={styles('address')}>
							<p>{school.address.street}</p>
							<p>{school.address.city} {school.address.zipcode}</p>
						</div>

					}
				</div>
				{school.class_schedule &&
					<Table 
						data={this.classes_table}
					/>
				}
			</section>
		);
	}

	render() {
		const {
			router
		} = this.props;

		return (
			<>
				<Header router={router} />
				<HeroBanner 
					className={styles('herobanner')}
					title='Classes' 
					text_position={HeroBanner.TextPosition.BOTTOM}

					background_image='https://biggica-sites.s3.amazonaws.com/fongs-hung-ga/fongs-doyers-pose.jpg'
					background_position={HeroBanner.BackgroundPosition.CENTER}
					hero_height={HeroBanner.HeroHeight.HALF} 
					gradient={HeroBanner.Gradient.BOTTOM}
				/>
				<Layout>
					<PageHeader 
						className={styles('page-header')}
						title='Class Schedules'
					/>
					{schools.locations && schools.locations.map(this.renderSchool)}
				</Layout>
			</>
		)
	}
}

export default withRouter(Classes);
