import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import Image from 'components/image';

import styles from './ArticleHeader.scss';

class ArticleHeader extends PureComponent {

	static propTypes = {
		className: PropTypes.string,
		title: PropTypes.string,
		byline: PropTypes.object,
		primary_asset: PropTypes.object
	}

	renderByline = byline => {
		<div className={styles('byline')}>
			{byline.image && 
				<div className={styles('image')}>
					<Image
						style={{
							width: '100%',
						}}
						fill_mode={Image.FillMode.COVER}
						src={byline.image}
						alt={byline.name}
					/>
				</div>
			}
			<span className={styles('name')}>
				{ byline.url ? 
					<a href={byline.url}> 
						{byline.name}
					</a> : byline.name
				}
			</span>
		</div>

	}

	renderPrimaryAsset = asset => {
		switch(asset.type) {
			case 'image':
				return (
					<Image
						style={{
							width: '100%',
						}}
						fill_mode={Image.FillMode.COVER}
						src={asset.src}
						alt={asset.caption}
					/>
				);
			default:
				return <div />;
		}
	}


	render() {
		const {
			className,
			title,
			byline,
			primary_asset
		} = this.props;

		return (
			<div className={classnames(styles('container'), className)}>
				{ title && <h1 className={styles('title')}>{title}</h1> }
				{ byline && this.renderByline(byline) }
				{ primary_asset && 
					<div className={styles('primary-asset-container')}>
						{this.renderPrimaryAsset(primary_asset)}
					</div> 
				}
			</div>
		);	
	}
}

export default ArticleHeader;
