import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';

import styles from './Table.scss';


class Table extends PureComponent {

	static propTypes = {
		title: PropTypes.string,
		description: PropTypes.string,
		data: PropTypes.object,
		color: PropTypes.string,
	}

	renderHeaders = (header, index) => {
		return <div key={`${header.name}-${index}`}>{header.name}</div>;
	}

	renderRows = (index, row) => {
		return <div>{row.name}</div>
	}

	render() {
		const {
			title,
			description,
			data,
		} = this.props;
		console.log(data);
		return (
			<div className={styles('container')}>
				{title && <h3 className={styles('title')}>{title}</h3>}
				<div className={styles('table-container')}>
					<div className={styles('table-headers')}>
						{data.headers && data.headers.map(this.renderHeaders)}
					</div>
					<div className={styles('table-rows')}>
						{data.rows && data.rows.map(this.renderRows)}
					</div>
				</div>
			</div>
		);	
	}
}

export default Table;
