import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';

import styles from './Article.scss';

class Article extends PureComponent {

	static propTypes = {
		className: PropTypes.string,
		article_data: PropTypes.object
	}

	renderTitle = data => {
		return <h1 className={styles('title')}>{data.content}</h1>;
	}

	renderSubHeader = data => {
		return <h3 className={styles('subtitle')}>{data.content}</h3>;
	}

	renderParagraph = data => {
		return <p className={styles('paragraph')}>{data.content}</p>
	}

	renderImage = data => {
		const size = data.size;
		const image_container_classname = styles('image-container', {size});

		return (
			<div className={image_container_classname}>
			</div>
		);
	}

	renderItems = (item, index) => {
		switch(item.type) {
			case 'title':
				this.renderTitle(item.data);

			default:
				return <div />;
		}
	}


	render() {
		const {
			className,
			article_data
		} = this.props;
		console.log(article_data)
		return (
			<div className={classnames(styles('container'), className)}>
				{article_data.title && this.renderTitle(article_data.title)}
				{article_data.body_items.map(this.renderItems)}
			</div>
		);	
	}
}

export default Article;
