import { Action } from 'actions/actions';

// REDUCERS
export const reducer = (state = {}, action) => {

	const { meta, payload } = action;

	switch (action.type) {
		case 'FETCH_ARTICLE_SUCCESS': {
			const { menu, site_links, social_links } = payload;
			const edition = meta.edition;

			console.log('SHELL SUCCESS!');

			return {
				...state,
				menu,
				site_links,
				social_links,
				edition,
			};
		}

		default:
			return state
	}
}
